LedScreen::LedScreen() {
  LedScreen::neoPixels = new Adafruit_NeoPixel(SCREENPIXELS, lightPin, NEO_GRB + NEO_KHZ800);
  LedScreen::neoPixels->begin();
  LedScreen::neoPixels->show();
}

void LedScreen::clearScreen() {
  LedScreen::neoPixels->clear();
  LedScreen::neoPixels->show();
}

void LedScreen::drawMario() {
  drawPictureArray(MarioSerie[pictureIndex], Mario);
  pictureIndex++;
  if(pictureIndex > 3)
    pictureIndex = 0;
}

void LedScreen::drawCreeper() {
  pictureIndex = 0;
  drawPictureArray(CreeperSerie[pictureIndex], Creeper);
}

void LedScreen::drawGhost() {
  drawPictureArray(GhostSerie[pictureIndex], Ghost);
  pictureIndex++;
  if(pictureIndex > 1)
    pictureIndex = 0;
}

void LedScreen::drawGoombas() {
  drawPictureArray(GoombasSerie[pictureIndex], Goombas);
  pictureIndex++;
  if(pictureIndex > 1)
    pictureIndex = 0;
}

void LedScreen::drawPacMan() {
  drawPictureArray(PacManSerie[pictureIndex], PacMan);
  pictureIndex++;
  if(pictureIndex > 3)
    pictureIndex = 0;
}

void LedScreen::drawPigFace() {
  pictureIndex = 0;
  drawPictureArray(PigFaceSerie[pictureIndex], PigFace);
}

void LedScreen::drawPictureArray(byte PictureIndex, const byte pictureArray[][16][16][3]) {
    short flipFactor;
    short index = 0;
    for(int x = 0; x <= 15; x++) {
      for(int y = 0; y <= 15; y++) {
      
        if(x % 2)
          flipFactor = 15-y;
        else
          flipFactor = y;

        unsigned char r = pictureArray[PictureIndex][x][flipFactor][0]*intensity;
        unsigned char g = pictureArray[PictureIndex][x][flipFactor][1]*intensity;
        unsigned char b = pictureArray[PictureIndex][x][flipFactor][2]*intensity;
        uint32_t pixelColorValue = (b) + (g <<8) + (r << 16) + (0 << 24);
      
        LedScreen::neoPixels->fill(pixelColorValue, index,1);
        index++;
      }
    }
    LedScreen::neoPixels->show();
}


void LedScreen::drawPicture(short picNr) {
    short flipFactor;
    short index = 0;
    for(int x = 0; x <= 15; x++) {
      for(int y = 0; y <= 15; y++) {
      
        if(x % 2)
          flipFactor = 15-y;
        else
          flipFactor = y;

        unsigned char r = Mario[picNr][x][flipFactor][0]*intensity;
        unsigned char g = Mario[picNr][x][flipFactor][1]*intensity;
        unsigned char b = Mario[picNr][x][flipFactor][2]*intensity;
        uint32_t pixelColorValue = (b) + (g <<8) + (r << 16) + (0 << 24);
      
        LedScreen::neoPixels->fill(pixelColorValue, index,1);
        index++;
      }
    }
    LedScreen::neoPixels->show();
}

void LedScreen::drawPixel(byte x, byte y, byte colorR, byte colorG, byte colorB) {
  uint32_t pixelColorValue = (colorB) + (colorG <<8) + (colorR << 16) + (0 << 24);
  if (y & 1) {
    x = 15-x;
  }  
  int index = (y*16) + x;  
  LedScreen::neoPixels->fill(pixelColorValue, index,1);
}

void LedScreen::draw7PixelDigit(byte digit, byte offsetX, byte offsetY, byte colorR, byte colorG, byte colorB) {
  for(int y=0; y<=6; y++) {
    byte digitLine = numbersPixelSize7[digit][y];
    for(int x = 6; x>=0; x--) {
      if(digitLine & 1)
        drawPixel(x+offsetX,y+offsetY,colorR,colorG,colorB);
      else
        drawPixel(x+offsetX,y+offsetY,0,0,0);
      digitLine = digitLine >> 1;
    }
  }
  LedScreen::neoPixels->show();
}
